import "core-js/modules/es6.array.iterator";
import "core-js/modules/es6.promise";
import "core-js/modules/es7.promise.finally";
import Vue from 'vue';
import vueRouter from 'vue-router';
import store from '@/store';
Vue.use(vueRouter);

var Home = function Home(resolve) {
  return require(['../components/Index'], resolve);
};

var LoginPage = function LoginPage(resolve) {
  return require(['../components/LoginPage'], resolve);
};

var NotFound = function NotFound(resolve) {
  return require(['../components/NotFound'], resolve);
};

var routes = [// 后台内容
{
  name: 'Home',
  path: '/',
  component: Home
}, // 登陆
{
  name: 'login',
  path: '/login',
  component: LoginPage
}, // 404
{
  name: '/notFound',
  path: '*',
  meta: {
    requireAuth: true
  },
  component: NotFound
}];
var router = new vueRouter({
  routes: routes
});

if (localStorage.getItem('token') || localStorage.getItem('rid')) {
  store.commit('moduleLogin/login');
}

router.beforeEach(function (to, from, next) {
  if (!store.state.moduleLogin.isLogin && to.path !== '/login') {
    next('/login');
  } else {
    next();
  }
});
export default router;
