import { addProdTypeAPI, PutProdTypeAPI, getProdTypeDetailAPI } from '@/api/inventoryRecordAPI.js';
export default {
  name: 'editor',
  data: function data() {
    return {
      // 规格列表
      level: '1',
      typeSearch: [],
      pgIdList: [],
      onetypeidlist: [],
      dataForm: {
        title: '',
        remark: ''
      },
      imageShowUrl: '' // resourcesUrl: window.SITE_CONFIG.resourcesUrl

    };
  },
  components: {},
  props: {
    prodtypeId: {
      required: true
    }
  },
  methods: {
    dataFormSubmit: function dataFormSubmit() {
      var _this = this;

      this.$refs['dataForm'].validate(function (valid) {
        var args = {
          title: _this.dataForm.title,
          remark: _this.dataForm.remark
        }; // console.log(this.prodchangeType)

        if (_this.prodtypeId == 0) {
          addProdTypeAPI(args).then(function (res) {
            var data = res.data;

            if (data.code === 200) {
              _this.$message({
                type: 'success',
                message: '添加分类成功'
              });

              _this.backhtmls();

              _this.$emit('finish-action');
            } else {
              _this.$message.error(res.data.msg);
            }
          }).catch(function (err) {});
        } else {
          args.tid = _this.dataForm.tid;
          PutProdTypeAPI(args).then(function (res) {
            var data = res.data;

            if (data.code === 200) {
              _this.$message({
                type: 'success',
                message: '修改分类成功'
              });

              _this.backhtmls();

              _this.$emit('finish-action');
            } else {
              _this.$message.error(res.data.msg);
            }
          }).catch(function (err) {});
        }
      });
    },
    getProdTypeDetail: function getProdTypeDetail(id) {
      var _this2 = this;

      console.log("getProdTypeDetail");
      var params = id;
      getProdTypeDetailAPI(params).then(function (res) {
        console.log("getProdTypeDetailAPI data");
        var prodtypeform = res.data.data;
        _this2.dataForm = prodtypeform;
        console.log(_this2.dataForm);
      }).catch(function (err) {//this.tableLoading = false;
      });
    },

    /*
      * 返回上层
      */
    backhtmls: function backhtmls() {
      this.$emit('close-form');
    }
  },
  mounted: function mounted() {
    console.log(this.prodtypeId);

    if (this.prodtypeId > 0) {
      this.getProdTypeDetail(this.prodtypeId);
    }
  }
};
