import { saveQuestionTypeAPI, updateQuestionTypeAPI, getQuestionTypeDetailAPI } from '@/api/questionAPI.js';
export default {
  name: 'editor',
  data: function data() {
    return {
      // 规格列表
      dataForm: {
        name: '',
        sort: 0
      }
    };
  },
  components: {},
  props: {
    updateId: {
      required: true
    }
  },
  methods: {
    dataFormSubmit: function dataFormSubmit() {
      var _this = this;

      this.$refs['dataForm'].validate(function (valid) {
        var args = {
          name: _this.dataForm.name,
          sort: _this.dataForm.sort
        }; // console.log(this.prodchangeType)

        if (_this.updateId == 0) {
          saveQuestionTypeAPI(args).then(function (res) {
            var data = res.data;

            if (data.code === 200) {
              _this.$message({
                type: 'success',
                message: '添加分类成功'
              });

              _this.backhtmls();

              _this.$emit('finish-action');
            } else {
              _this.$message.error(res.data.msg);
            }
          }).catch(function (err) {});
        } else {
          args.id = _this.updateId;
          updateQuestionTypeAPI(args).then(function (res) {
            var data = res.data;

            if (data.code === 200) {
              _this.$message({
                type: 'success',
                message: '修改分类成功'
              });

              _this.backhtmls();

              _this.$emit('finish-action');
            } else {
              _this.$message.error(res.data.msg);
            }
          }).catch(function (err) {});
        }
      });
    },
    getProdTypeDetail: function getProdTypeDetail(id) {
      var _this2 = this;

      console.log("getProdTypeDetail");
      var params = id;
      getQuestionTypeDetailAPI(params).then(function (res) {
        console.log("getProdTypeDetailAPI data");
        var prodtypeform = res.data.data;
        _this2.dataForm = prodtypeform;
        console.log(_this2.dataForm);
      }).catch(function (err) {//this.tableLoading = false;
      });
    },

    /*
      * 返回上层
      */
    backhtmls: function backhtmls() {
      this.$emit('close-form');
    }
  },
  mounted: function mounted() {
    console.log(this.updateId);

    if (this.updateId > 0) {
      this.getProdTypeDetail(this.updateId);
    }
  }
};
