const SecondIndex = () => import('./prodtypeInfo.vue');
import {
    getProdtypeLISTAPI,
    deleteProdtypeAPI
} from '@/api/inventoryRecordAPI.js'

export default {
    name:'prodList',
    data(){
        return{
            form:{
                size:10,
                current:1,
                sortType: 0,
            },
            tableData:[],
            totalCount:0,
            tableLoading:false,
            isEdit:false,//是否编辑
            typeId:0,//分类ID
            secondDepShow:false,//产品1弹窗
        };
    },
    components:{
        // AddDepModal,
        SecondIndex
    },
    methods:{
        /*
         * 获取产品分类列表
         */
        getProdtypeList() {
            const params = {...this.form};

            this.tableLoading = true;
            getProdtypeLISTAPI(params)
                .then(res => {

                    const data = res.data.data;
                    this.totalCount = data.length;
                    this.tableData = data;
                    this.tableLoading = false;

                })
                .catch(err => {
                    this.tableLoading = false;
                })
        },
        /**
         * @msg: 翻页
         * @param {type} 
         * @return: 
         */
        handleSizeChange(cSize) {
            this.form.size = cSize;
            this.getProdtypeList();
        },

        handleCurrentChange(currentPage) {
            this.form.current = currentPage;
            this.getProdtypeList();
        },
        /**
         * @msg: 新增产品分类
         * @param {type} 
         * @return: 
         */
        handleAddType(typeids){
            console.log("handleAddType typeids:"+typeids);
            this.typeId = typeids;
            this.isEdit = false;
            this.secondDepShow = true;
        },
         /**
         * @msg: 修改产品分类
         * @param {type} 
         * @return: 
         */
        handleChange(typeids){
            console.log("handleChange typeids:"+typeids);
            this.typeId = typeids;
            this.isEdit = true;
            this.secondDepShow = true;
        },
        /**
         * @msg: 修改产品分类
         * @param {Number} row  修改科室的信息

        handleChange(changeid, row) {
            if(row==0){
                this.levelId=1;
            }else{
                this.levelId=2;
            }
            this.typeId = changeid;
            this.changtype=2;
            this.isEdit = false;
            this.secondDepShow = true;
        },
         */
        /**
         * @msg: 删除产品分类
         * @param {Number} row  删除产品分类
         */
        handleDel(index){
            deleteProdtypeAPI(index)
                .then(res => {
                    const response = res.data;
                    let status = 'success';

                    if (response.code === 200) {
                        this.getProdtypeList();
                    } else {
                        status = 'error';
                    }
                    this.$message({
                        type: status,
                        message: status === 'success' ? '删除成功！' : response.msg,
                    })
                })
        },
        addProtype(){
            this.handleAddType(0)
            // this.secondDepShow = true;
        }
    },
    created(){
        this.getProdtypeList()
    }
}
