import {
     getQuestionDetailAPI,
     updateQuestionAPI,
     saveQuestionAPI,
     getQuestionTypeListAPI
} from '@/api/questionAPI.js';
import axios from 'axios';
import E from 'wangeditor';
export default {
    name:'prodList',
    data(){
        return{
            dataForm:{
                title:'',
                content:'',
                typeId:''
            },
            form:{
                size:50,
                current:1
                // statusType:'1'
            },
            item:{},
            typelist:[],
            rules: {
                
            },
        }
    },
    components:{
        // AddDepModal,
    },
    props: {
        updateId: {
            required: true,
        },
    },
    methods:{
        
        /*
         * 获取代理等级详情
         */
        getDetail(id){
            this.tableLoading = true;
             getQuestionDetailAPI(id)
                .then(res => {
                    const orderdata=res.data.data
                    console.log(orderdata)
                    this.item=orderdata;
                    this.dataForm=this.item;
                       
                    var editor = new E('#editorElem')
                    editor.customConfig.uploadImgShowBase64 = true //图片以base64形式保存
                    editor.customConfig.onchange = (html) => {
                        this.dataForm.content = html
                    }
                    editor.customConfig.customUploadImg = (files, insert) => {
                        const formData = new FormData();
                        formData.append('files', files[0]);
                        axios.post("http://admin.zhanchengwlkj.com:8080/distribution_backstage/file/addFile", formData, {
                            headers: {
                                "Content-Type": "multipart/form-data"
                            }
                            }).then(res => {
                            let { data } = res
                            insert(data.data)  
                            // 插入图片到editor
                            });
                        // formData.append('path', 'files/ne-icev3-dashboard/content/content/');
                        
                        };
                        editor.customConfig.uploadImgShowBase64 = true //图片以base64形式保存
                        editor.create()
                        editor.txt.html(this.dataForm.content)
                    })
                    .catch(err => {
                        
                    })
        },
        getQuestionTypeList(){
            const params = {...this.form};
             getQuestionTypeListAPI(params)
                .then(res => {
                    console.log(res.data.data);
                    const data = res.data.data;
                    this.typelist = data;
                })
                .catch(err => {
                   
                })
        },
         // 表单提交
        dataFormSubmit () {
            console.log("title:"+this.dataForm.title+" id:"+this.updateId+" content:"+this.dataForm.content);
            let that=this;
                
                const putdata={
                    title: this.dataForm.title,
                    content:this.dataForm.content,
                    typeId:this.dataForm.typeId
                }
                if(this.updateId==0){
                    saveQuestionAPI(putdata)
                            .then(res => {
                                const data = res.data;
                                if (data.code === 200) {
                                        this.$message({
                                            type: 'success',
                                            message: '添加成功',
                                        });
                                    this.backhtmls()
                                        this.$emit('finish-action')
                                }else{
                                    this.$message.error(res.data.msg)
                                }
                            })
                            .catch(err => {
                            });
                }else{
                    putdata.id=this.updateId
                    updateQuestionAPI(putdata)
                                    .then(res => {
                                        console.log(res);
                                        const sucresponse = res.data;
                                        if(sucresponse.code==200){
                                            this.$message({
                                            type: 'success',
                                            message: '修改成功',
                                            });
                                            this.$emit('close-form')
                                            this.$emit('finish-action')
                                        }else{
                                            this.errorMsg(sucresponse.message)
                                        }
                                    })
                                    .catch(err => {
                                        console.log(err)
                                        this.tableLoading = false;
                                    }) 
                }
            
        
        },
        /*
        * 返回上层
        */
        backhtmls(){
            this.$emit('close-form')
        }    
    },
    mounted(){
        if(this.updateId==0){
            var editor = new E('#editorElem')
            editor.customConfig.uploadImgShowBase64 = true //图片以base64形式保存
            editor.customConfig.onchange = (html) => {
            this.dataForm.content = html
            }
            editor.customConfig.customUploadImg = (files, insert) => {
            const formData = new FormData();
            formData.append('files', files[0]);
            axios.post("http://admin.zhanchengwlkj.com:8080/distribution_backstage/file/addFile", formData, {
                headers: {
                    "Content-Type": "multipart/form-data"
                }
                }).then(res => {
                let { data } = res
                insert(data.data)  
                // 插入图片到editor
                });
            // formData.append('path', 'files/ne-icev3-dashboard/content/content/');
            
            };
            editor.customConfig.uploadImgShowBase64 = true //图片以base64形式保存
            editor.create()
        }else{
            this.getDetail(this.updateId)
        }
         
        
        this.getQuestionTypeList();
        
    }
}
