import {
     timeStamp2String
} from '@/api/globalAPI.js'
import {
     getVistitorsListAPI,
     delteRecordAPI
} from '@/api/visitorsRecordAPI.js'
export default {
    name:'prodList',
    data(){
        return{
            form:{
                size:10,
                current:1,
                // name:'',
                // startTime:'',
                // endTime:'',
            },
            value1:[, ],
            id:'',
            ip:'',
            phone:'',
            nickname:'',
            storeName:'',
            tableData:[],
            totalCount:0,
            tableLoading:false,
            secondDepShow:false
        };
    },
    components:{
    },
    methods:{
        /**
         * @msg: 时间处理
         * @param {type} 
         * @return: 
         */
        timechange(time,type){
            return timeStamp2String(time,type)
        },
        /**
         * @msg: 翻页
         * @param {type} 
         * @return: 
         */
        handleSizeChange(cSize) {
            this.form.size = cSize;
            this.getVisitorsRecordList();
        },
        handleCurrentChange(currentPage) {
            this.form.current = currentPage;
            this.getVisitorsRecordList();
        },
        getVisitorsRecordList(){
            const params = {...this.form};
            if(this.value1[0]!=''&&this.value1[0]!=undefined&&this.value1[0]!=null){
                params.endTime=timeStamp2String(this.value1[1],2)
                params.startTime=timeStamp2String(this.value1[0],2)
            }
            this.tableLoading = true;
             getVistitorsListAPI(params)
                .then(res => {
                    const data = res.data.data;
                    console.log(data.records)
                    this.totalCount = data.total;
                    this.tableData = data.records;
                    this.tableLoading = false;
                })
                .catch(err => {
                    this.tableLoading = false;
                })
        },
        delteRecord(id){
            this.$confirm('确定要删除该记录么？（此操作不可逆）', '提示', {
                confirmButtonText: '确定',
                cancelButtonText: '取消',
                type: 'warning'
            })
                .then(() => {
                    delteRecordAPI(id)
                    .then(res => {
                        if(res.data.code === 200){
                            this.$message({
                                type: 'success',
                                message: '该记录已经删除',
                            });
                            this.getVisitorsRecordList()
                        }else{
                            this.$message.error(res.data.msg)
                        }                   
                    })
                    .catch(err => {
                    })
                })
        }
    },
    created(){
        this.getVisitorsRecordList()
    }
}
